import React, { useContext, useState } from "react";
import "./Css/ShopCategory.css";
import { ShopContext } from "../Context/ShopContext";
import dropdown_icon from "../Components/Assets/dropdown_icon.png";
import Item from "../Components/item/item";
import FilterSidebar from "../Components/Filters/FilterSidebar";

const ShopCategory = (props) => {
  const { all_product } = useContext(ShopContext);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");

  const filteredProducts = all_product.filter((item) => {
    const matchesMainCategory = props.category.toLowerCase() === item.category.toLowerCase();
    const matchesFilter = selectedCategory === "All" || item.category === selectedCategory;
    const matchesSearch = item.name.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesMainCategory && matchesFilter && matchesSearch;
  });

  return (
    <div className="shop-category">
      {/* üîº Banner */}
      <img className="Shopcategory-banner" src={props.banner} alt="banner" />

      {/* üîç Search + Filter Section */}
      <div className="shopcategory-top">
       
        <FilterSidebar selectedCategory={selectedCategory} setSelectedCategory={setSelectedCategory} />
      </div>

      {/* üì¶ Sort + Product Grid */}
      <div className="shopCategory-indexSort">
        <p>
          Showing <span>{filteredProducts.length}</span> of {all_product.length} products
        </p>
        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="dropdown" />
        </div>
      </div>

      {/* üõí Products */}
      <div className="shopcategory-product">
        {filteredProducts.map((item, i) => (
          <Item key={i} {...item} />
        ))}
      </div>

      <div className="shopcategory-loadmore">Explore More</div>
    </div>
  );
};

export default ShopCategory;
