import React, { useState } from 'react';
import "./LatestCollection.css";
import all_product from '../../Assets/all_product';
import Item from "../item/item"; // if this is the right path
const LatestCollection = () => {
  const [selectedCategory, setSelectedCategory] = useState("All");

  const filteredProducts = selectedCategory === "All"
    ? all_product
    : all_product.filter(item => item.category === selectedCategory);

  return (
    <div className="flex gap-6 px-8 py-6">
      {/* Sidebar */}
      <div className="w-1/4">
        <h2 className="text-xl font-semibold mb-4">Filters</h2>
        <h3 className="text-lg font-medium mb-2">Categories</h3>
        <div className="flex flex-col gap-2">
          <label>
            <input type="radio" name="category" onChange={() => setSelectedCategory("All")} /> All
          </label>
          <label>
            <input type="radio" name="category" onChange={() => setSelectedCategory("Mens")} /> Men
          </label>
          <label>
            <input type="radio" name="category" onChange={() => setSelectedCategory("Womens")} /> Women
          </label>
          <label>
            <input type="radio" name="category" onChange={() => setSelectedCategory("Kids")} /> Kids
          </label>
        </div>
      </div>

      {/* Products */}
      <div className="w-3/4">
        <h2 className="text-xl font-semibold mb-4">Latest Collection</h2>
        {filteredProducts.length > 0 ? (
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            {filteredProducts.map(product => (
              <div key={product.id} className="border p-3 rounded-md shadow-sm">
                <img src={product.image} alt={product.name} className="h-48 object-cover w-full mb-2" />
                <h4 className="font-medium">{product.name}</h4>
                <p className="text-sm text-gray-600">${product.new_price}</p>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-red-500 font-medium">No latest items found in this category.</p>
        )}
      </div>
    </div>
  );
};

export default LatestCollection;
