import "./App.css";

/* ---------- Layout + Navigation ---------- */
import Navbar from "./Components/Navbar/Navbar";
import Footer from "./Components/Footer/Footer";

/* ---------- React Router ---------- */
import { BrowserRouter, Routes, Route } from "react-router-dom";

/* ---------- Pages ---------- */

import Shop from "./Pages/Shop";
import ShopCategory from "./Pages/ShopCategory";
import Product from "./Pages/Product";
import Cart from "./Pages/Cart";
import Loginsignup from "./Pages/Loginsignup";

/* ---------- Collection Page ---------- */
import LatestCollection from "./Components/LatestCollection/LatestCollection";
import SearchResults from "./Pages/SearchResults";
import AdminAuth from "./Pages/adminauth"; 

/* ---------- Toast Notifications ---------- */
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  return (
    <BrowserRouter>
      {/* Global Toast for Notifications */}
      <ToastContainer 
        position="top-right" 
        autoClose={3000}
        pauseOnHover={true}
        theme="light"
      />

      {/* Navbar stays at the top on all pages */}
      <Navbar />

      {/* Main Routes */}
      <Routes>

        <Route path="/shop" element={<Shop />} />
        <Route path="/category/:categoryName" element={<ShopCategory />} />
        <Route path="/latest-collection" element={<LatestCollection />} />
        <Route path="/product/:ProductId" element={<Product />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/login" element={<Loginsignup />} />
        <Route path="/search" element={<SearchResults />} />
        <Route path="/adminauth" element={<AdminAuth />} />
      </Routes>

      {/* Footer stays at the bottom on all pages */}
      <Footer />
    </BrowserRouter>
  );
}

export default App;
