import React, { useState, useEffect } from "react";
import "./LatestCollection.css";
import all_product from '../../Assets/all_product';




import Item from "../item/item"; // if this is the right path

const LatestCollection = () => {
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedTypes, setSelectedTypes] = useState([]);

  // ✅ Initial render: filter products with category === 'latest'
  const initialProducts = all_product.filter((item) => item.category === "latest");

  // ✅ Checkbox handler
  const handleCategoryChange = (e) => {
    const value = e.target.value;
    setSelectedCategories((prev) =>
      prev.includes(value) ? prev.filter((cat) => cat !== value) : [...prev, value]
    );
  };

  const handleTypeChange = (e) => {
    const value = e.target.value;
    setSelectedTypes((prev) =>
      prev.includes(value) ? prev.filter((type) => type !== value) : [...prev, value]
    );
  };

  // ✅ Filter logic based on selected checkboxes
  const filteredProducts = initialProducts.filter((product) => {
    const categoryMatch =
      selectedCategories.length === 0 || selectedCategories.includes(product.gender);
    const typeMatch =
      selectedTypes.length === 0 || selectedTypes.includes(product.type);
    return categoryMatch && typeMatch;
  });

  return (
    <div className="latest-collection-container">
      <div className="sidebar">
        <h3>Filters</h3>
        <div className="filter-section">
          <h4>Categories</h4>
          <label>
            <input type="checkbox" value="mens" onChange={handleCategoryChange} />
            Men
          </label>
          <label>
            <input type="checkbox" value="womens" onChange={handleCategoryChange} />
            Women
          </label>
          <label>
            <input type="checkbox" value="kids" onChange={handleCategoryChange} />
            Kids
          </label>
        </div>

        <div className="filter-section">
          <h4>Type</h4>
          <label>
            <input type="checkbox" value="topwear" onChange={handleTypeChange} />
            Topwear
          </label>
          <label>
            <input type="checkbox" value="bottomwear" onChange={handleTypeChange} />
            Bottomwear
          </label>
          <label>
            <input type="checkbox" value="winterwear" onChange={handleTypeChange} />
            Winterwear
          </label>
        </div>
      </div>

      <div className="main-content">
        <h2>Latest Collection</h2>
        {filteredProducts.length > 0 ? (
          <div className="product-grid">
            {filteredProducts.map((item) => (
              <Item
                key={item.id}
                id={item.id}
                name={item.name}
                image={item.image}
                new_price={item.new_price}
                old_price={item.old_price}
              />
            ))}
          </div>
        ) : (
          <p>No latest items found in this category.</p>
        )}
      </div>
    </div>
  );
};

export default LatestCollection;
