import "./App.css";

/* ---------- Layout + Navigation ---------- */
import Navbar from "./Components/Navbar/Navbar";
import Footer from "./Components/Footer/Footer";
import LatestCollection from "./Components/LatestCollection/LatestCollection";



/* ---------- React Router ---------- */
import { BrowserRouter, Routes, Route } from "react-router-dom";

/* ---------- Pages ---------- */
import Shop from "./Pages/Shop";
import ShopCategory from "./Pages/ShopCategory";
import Product from "./Pages/Product";
import Cart from "./Pages/Cart";
import Loginsignup from "./Pages/Loginsignup";
import About from "./Pages/About";           // ✅ Added About
import Contact from "./Pages/Contact";       // ✅ Added Contact
import Company from "./Pages/Company";       // ✅ Added Company

/* ---------- Category banner images ---------- */
import bannerMen from './Assets/banner_men.png';
import bannerWomen from './Assets/banner_women.jpg';
import bannerKids from './Assets/banner_kids.jpg';
/* ─────────────────────────────────────────────
   App Component
   - Defines global layout (Navbar + Footer)
   - Sets up all React Router routes
────────────────────────────────────────────── */
function App() {
  return (
    <BrowserRouter>
      {/* Top navigation bar (visible on all pages) */}
      <Navbar />

      {/* Main routing table */}
      <Routes>
        {/* Home page */}
        <Route path="/" element={<Shop />} />

        {/* Category pages */}
        <Route
          path="/mens"
          element={<ShopCategory banner={men_banner} category="Mens" />}
        />
        <Route
          path="/womens"
          element={<ShopCategory banner={women_banner} category="Womens" />}
        />
        <Route
          path="/kids"
          element={<ShopCategory banner={kid_banner} category="Kids" />}
        />

        {/* Product Details Page */}
        <Route path="/product/:ProductId" element={<Product />} />

        {/* Cart & Login */}
        <Route path="/cart" element={<Cart />} />
        <Route path="/login" element={<Loginsignup />} />

        {/* ✅ Extra Static Pages */}
        <Route path="/about" element={<About />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="/company" element={<Company />} />

   <Route path="/latest-collection" element={<LatestCollection />} />

      </Routes>

      {/* Footer (visible on all pages) */}
      <Footer />
    </BrowserRouter>
  );
}

export default App;
