import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import './adminauth.css';

function AdminAuth() {
  const [isRegistering, setIsRegistering] = useState(false);
  const [formData, setFormData] = useState({ name: "", email: "", password: "" });
  const navigate = useNavigate();

  const handleSubmit = async () => {
    const url = isRegistering ? "http://localhost:5000/admin/register" : "http://localhost:5000/admin/login";

    try {
      const response = await axios.post(url, formData);
      alert(response.data.message);

      if (!isRegistering) {
        navigate("/"); // Redirect to shop page after login
      }
    } catch (err) {
      alert(err.response?.data?.message || "Something went wrong");
    }
  };

  return (
    <div className="admin-auth-container">
      <div className="admin-auth-card">
        <h2>{isRegistering ? "Admin Register" : "Admin Login"}</h2>
        {isRegistering && (
          <input
            type="text"
            placeholder="Name"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          />
        )}
        <input
          type="email"
          placeholder="Email"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
        />
        <input
          type="password"
          placeholder="Password"
          value={formData.password}
          onChange={(e) => setFormData({ ...formData, password: e.target.value })}
        />
        <button onClick={handleSubmit}>
          {isRegistering ? "Register" : "Login"}
        </button>
        <p onClick={() => setIsRegistering(!isRegistering)}>
          {isRegistering ? "Already have an account? Login" : "Don't have account? Register"}
        </p>
      </div>
    </div>
  );
}

export default AdminAuth;
