import "./App.css";

/* ---------- Layout + Navigation ---------- */
import Navbar from "./Components/Navbar/Navbar";
import Footer from "./Components/Footer/Footer";

/* ---------- React Router ---------- */
import { BrowserRouter, Routes, Route } from "react-router-dom";

/* ---------- Pages ---------- */
import Shop from "./Pages/Shop";
import ShopCategory from "./Pages/ShopCategory";
import Product from "./Pages/Product";
import Cart from "./Pages/Cart";
import Loginsignup from "./Pages/Loginsignup";

/* ---------- Category banner images ---------- */
import men_banner from './Assets/banner_men.png';
import women_banner from './Assets/banner_women.jpg';
import kid_banner from './Assets/banner_kids.jpg';

/* ─────────────────────────────────────────────
   App Component
   - Defines global layout (Navbar + Footer)
   - Sets up all React Router routes
────────────────────────────────────────────── */
function App() {
  return (
    <BrowserRouter>
      {/* Top navigation bar (visible on all pages) */}
      <Navbar />

      {/* Main routing table */}
      <Routes>
        {/* Home page */}
        <Route path="/" element={<Shop />} />

        {/* Category pages, each with its own banner prop */}
        <Route
          path="/mens"
          element={<ShopCategory banner={men_banner} category="Mens" />}
        />
        <Route
          path="/womens"
          element={<ShopCategory banner={women_banner} category="Womens" />}
        />
        <Route
          path="/kids"
          element={<ShopCategory banner={kid_banner} category="Kids" />}
        />

        {/* Dynamic product page (e.g., /product/123) */}
        <Route path="/product/:ProductId" element={<Product />} />

        {/* Cart, Login pages */}
        <Route path="/cart"  element={<Cart />} />
        <Route path="/login" element={<Loginsignup />} />
      </Routes>

      {/* Footer (visible on all pages) */}
      <Footer />
    </BrowserRouter>
  );
}

export default App; // Default export so index.jsx can render <App />
