import React, { useContext, useState } from "react";
import "./Navbar.css";
import logo from "../Assets/logo.jpg";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import { Link } from "react-router-dom";
import { ShopContext } from "../../Context/ShopContext";
import { FaSearch } from "react-icons/fa"; // 🔍 search icon

const Navbar = () => {
  const [menu, setMenu] = useState("Shop");
  const { getTotalCartItems } = useContext(ShopContext);

  const [searchQuery, setSearchQuery] = useState("");

  const handleSearch = () => {
    if (searchQuery.trim() !== "") {
      console.log("Searching for:", searchQuery); // 🔍 Yeh future me search page pe redirect kar sakta hai
    }
  };

  return (
    <div className="navbar">
      {/* ── Logo ── */}
      <div className="nav-logo">
        <img src={logo} alt="Logo" />
        <p>SHOPPER</p>
      </div>

      {/* ── Menu ── */}
      <ul className="nav-menu">
        <li onClick={() => setMenu("Shop")}>
          <Link to="/" style={{ textDecoration: "none" }}>Shop</Link>
          {menu === "Shop" && <hr />}
        </li>
        <li onClick={() => setMenu("Mens")}>
          <Link to="/mens" style={{ textDecoration: "none" }}>Men</Link>
          {menu === "Mens" && <hr />}
        </li>
        <li onClick={() => setMenu("Womens")}>
          <Link to="/womens" style={{ textDecoration: "none" }}>Womens</Link>
          {menu === "Womens" && <hr />}
        </li>
        <li onClick={() => setMenu("Kids")}>
          <Link to="/kids" style={{ textDecoration: "none" }}>Kids</Link>
          {menu === "Kids" && <hr />}
        </li>
      </ul>

      {/* ── 🔍 Search Bar ── */}
      <div className="nav-search">
        <input
          type="text"
          placeholder="Search..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button onClick={handleSearch}>
          <FaSearch />
        </button>
      </div>

      {/* ── Login & Cart ── */}
      <div className="nav-login-cart">
        <Link to="/login"><button>Login</button></Link>
        <Link to="/cart">
          <ShoppingCartIcon style={{ fontSize: 30, color: "#333", cursor: "pointer" }} />
        </Link>
        <div className="nav-cart-count">{getTotalCartItems()}</div>
      </div>
    </div>
  );
};

export default Navbar;
