import React from "react";
import "./item.css";
import { Link } from "react-router-dom";
import StarRating from "../Rating/StarRating";

/*
  Item component:
  - Displays product card (image, name, prices)
  - Clicking the image (wrapped in <Link>) routes to `/product/:id`
    and scrolls the viewport to the top.
*/
const Item = (props) => {
  // Quick console log for troubleshooting image path
  console.log("Image path:", props.image);

  return (
    <div className="item">
      {/* Clicking the link navigates to product page */}
      <Link
        to={`/product/${props.id}`}
        onClick={() => window.scrollTo(0, 0)} // scroll to top after route change
      >
        <img src={props.image} alt={props.name} />
      </Link>

      {/* Product name */}
      <p>{props.name}</p>

      {/* New vs. old price (new highlighted) */}
      <div className="item-prices">
        <div className="item-price-new">${props.new_price}</div>
        <div className="item-price-old">${props.old_price}</div>
      </div>
       {/* ‚≠ê 2. Show star rating below price */}
      {rating && <StarRating rating={rating} />} 
    </div>
  );
};

export default Item; // Export so <Item /> can be reused in product grids
