// React aur navigate hook import kiya
import React from "react";
import { useNavigate } from "react-router-dom";  // navigate karne ke liye
import "./Pages/Loginsignup.css";           // styling ke liye CSS file import ki

// Function component banayap
const Loginsignup = () => {
  const navigate = useNavigate();  // page ko redirect karne ke liye use hota hai

  // Jab "Continue" button pe click kare to kya ho
  const handleContinue = () => {
    // Future me yahan form validation aur API call bhi laga sakte ho
    navigate("/");  // user ko "/" yaani home/shop page pe redirect kar do
  };

  return (
    <div className="loginsignup">  {/* outer container pink background ke liye */}
      <div className="loginsignup-container">  {/* white box jisme form hai */}
        <h1>Sign Up</h1>

        {/* input fields - name, email aur password */}
        <div className="loginsignup-fields">
          <input type="text" placeholder="Your Name" />
          <input type="email" placeholder="Email Address" />
          <input type="password" placeholder="Password" />
        </div>

        {/* Continue button - jab click ho to handleContinue chale */}
        <button onClick={handleContinue}>Continue</button>

        {/* Agar account hai to login ka option dikhana */}
        <p className="loginsignup-login">
          Already have an account? <span>Login here</span>
        </p>

        {/* Checkbox for terms and conditions */}
        <div className="loginsignup-agree">
          <input type="checkbox" />
          <p>By continuing I agree to the terms of use & privacy policy.</p>
        </div>
      </div>
    </div>
  );
};

// Component ko export kiya taaki use kar sake App me
export default Loginsignup;
