import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminAuth = () => {
  const [showLogin, setShowLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  // Toggle between login and register
  const toggleForm = () => {
    setShowLogin(!showLogin);
    setEmail('');
    setPassword('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = showLogin ? 'http://localhost:5000/api/admin/login' : 'http://localhost:5000/api/admin/register';

    try {
      const res = await axios.post(url, { email, password });
      alert(res.data.message);
      if (showLogin) {
        navigate('/'); // After login go to homepage
      }
    } catch (err) {
      alert(err.response?.data?.message || 'Something went wrong');
    }
  };

  return (
    <div style={{ padding: '30px' }}>
      <h2>{showLogin ? 'Admin Login' : 'Admin Registration'}</h2>
      <form onSubmit={handleSubmit}>
        <input type="email" placeholder="Email" value={email} required onChange={(e) => setEmail(e.target.value)} />
        <br /><br />
        <input type="password" placeholder="Password" value={password} required onChange={(e) => setPassword(e.target.value)} />
        <br /><br />
        <button type="submit">{showLogin ? 'Login' : 'Register'}</button>
      </form>
      <br />
      <button onClick={toggleForm}>
        {showLogin ? 'Switch to Register' : 'Switch to Login'}
      </button>
    </div>
  );
};

export default AdminAuth;
